                  <h4 className="text-sm font-medium mb-3">Time by Category</h4>
                  {statsLoading ? (
                    <div className="space-y-2">
                      {[...Array(5)].map((_, i) => (
                        <Skeleton key={i} className="h-10 w-full" />
                      ))}
                    </div>
                  ) : (
                    <div className="space-y-2">
                      {statsData
                        ?.sort((a, b) => b.totalMinutes - a.totalMinutes)
                        .slice(0, 6)
