const DEFAULT_EXCHANGE_RATE = 84;
const REFERRAL_BONUS_PERCENT = 20;

interface PriceTier {
  label: string;
  inr: number;
  usd: number;
}

const ECOSYSTEM_TIERS: PriceTier[] = [
  { label: "₹999 / $14.99", inr: 999, usd: 14.99 },
  { label: "₹1,999 / $29.99", inr: 1999, usd: 29.99 },
