  if (typeof window === "undefined") return "";
  try {
    let sessionId = localStorage.getItem("guestSessionId");
    if (!sessionId) {
      sessionId = crypto.randomUUID();
      localStorage.setItem("guestSessionId", sessionId);
    }
    return sessionId;
  } catch {
    return "";
