import React, { useState, useEffect } from "react";
import { Link, useLocation } from "wouter";
import { Moon, Sun, Menu, X, Globe, Wallet, LayoutDashboard, Shield, Sparkles, Activity, UserCircle, LogOut, Home, Rocket, Briefcase, Award, BookOpen, Info, LogIn, ChevronDown } from "lucide-react";
import { Button } from "@/components/ui/button";
import { CustomCursor } from "@/components/ui/custom-cursor";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

export function Layout({ children }: { children: React.ReactNode }) {
  const [location, setLocation] = useLocation();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [theme, setTheme] = useState<"light" | "dark">("light");

  // Simulated logged-in state (in production, this would come from auth context)
