import { useState } from "react";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
  DropdownMenuSeparator,
  DropdownMenuLabel,
} from "@/components/ui/dropdown-menu";
import { Share2, Twitter, Linkedin, Facebook, Link2, Check } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface ShareAchievementProps {
  rank: number;
  name: string;
  country: string;
  sector: string;
  tier: string;
  isCurrentUser?: boolean;
}

export function ShareAchievement({
  rank,
  name,
  country,
  sector,
  tier,
  isCurrentUser = false,
}: ShareAchievementProps) {
  const { toast } = useToast();
  const [copied, setCopied] = useState(false);

  const shareText = isCurrentUser
    ? `I'm ranked #${rank} on the i2u.ai Global Leaderboard! ğŸš€\n\n${tier} tier â€¢ ${sector} â€¢ ${country}\n\nJoin the revolution: Ideas to Unicorns through AI!\n\n#i2uai #Startup #Entrepreneur #Leaderboard`
    : `Check out ${name} ranked #${rank} on the i2u.ai Global Leaderboard! ğŸš€\n\n${tier} tier â€¢ ${sector} â€¢ ${country}\n\n#i2uai #Startup`;

  const shareUrl = typeof window !== "undefined" ? window.location.origin : "https://i2u.ai";

  const shareToTwitter = () => {
    const url = `https://twitter.com/intent/tweet?text=${encodeURIComponent(shareText)}&url=${encodeURIComponent(shareUrl)}`;
    window.open(url, "_blank", "width=550,height=420");
  };

  const shareToLinkedIn = () => {
    const url = `https://www.linkedin.com/sharing/share-offsite/?url=${encodeURIComponent(shareUrl)}&summary=${encodeURIComponent(shareText)}`;
    window.open(url, "_blank", "width=550,height=420");
  };

  const shareToFacebook = () => {
    const url = `https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(shareUrl)}&quote=${encodeURIComponent(shareText)}`;
    window.open(url, "_blank", "width=550,height=420");
  };

  const copyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(`${shareText}\n\n${shareUrl}`);
      setCopied(true);
      toast({
        title: "Copied!",
        description: "Achievement link copied to clipboard",
      });
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      toast({
        title: "Failed to copy",
        description: "Please try again",
        variant: "destructive",
      });
    }
  };

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button
          variant="ghost"
          size="sm"
          className="h-8 w-8 p-0 hover:bg-primary/10 hover:text-primary"
          data-testid={`button-share-${rank}`}
        >
          <Share2 className="h-4 w-4" />
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="end" className="w-48">
        <DropdownMenuLabel className="text-xs">Share Achievement</DropdownMenuLabel>
        <DropdownMenuSeparator />
        <DropdownMenuItem onClick={shareToTwitter} className="cursor-pointer" data-testid="share-twitter">
          <Twitter className="mr-2 h-4 w-4 text-[#1DA1F2]" />
          Twitter / X
        </DropdownMenuItem>
        <DropdownMenuItem onClick={shareToLinkedIn} className="cursor-pointer" data-testid="share-linkedin">
          <Linkedin className="mr-2 h-4 w-4 text-[#0A66C2]" />
          LinkedIn
        </DropdownMenuItem>
        <DropdownMenuItem onClick={shareToFacebook} className="cursor-pointer" data-testid="share-facebook">
          <Facebook className="mr-2 h-4 w-4 text-[#1877F2]" />
          Facebook
        </DropdownMenuItem>
        <DropdownMenuSeparator />
        <DropdownMenuItem onClick={copyToClipboard} className="cursor-pointer" data-testid="share-copy">
          {copied ? (
            <Check className="mr-2 h-4 w-4 text-green-500" />
          ) : (
            <Link2 className="mr-2 h-4 w-4" />
          )}
          {copied ? "Copied!" : "Copy Link"}
        </DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

server/paypal.ts.content:
import {
  Client,
  Environment,
  LogLevel,
  OAuthAuthorizationController,
  OrdersController,
} from "@paypal/paypal-server-sdk";
import { Request, Response } from "express";

const { PAYPAL_CLIENT_ID, PAYPAL_CLIENT_SECRET } = process.env;

export const isPayPalConfigured = !!(PAYPAL_CLIENT_ID && PAYPAL_CLIENT_SECRET);

let ordersController: OrdersController | null = null;
let oAuthAuthorizationController: OAuthAuthorizationController | null = null;

if (isPayPalConfigured) {
  const client = new Client({
    clientCredentialsAuthCredentials: {
      oAuthClientId: PAYPAL_CLIENT_ID!,
      oAuthClientSecret: PAYPAL_CLIENT_SECRET!,
    },
    timeout: 0,
    environment:
      process.env.NODE_ENV === "production"
        ? Environment.Production
        : Environment.Sandbox,
    logging: {
      logLevel: LogLevel.Info,
      logRequest: {
        logBody: true,
      },
      logResponse: {
        logHeaders: true,
      },
    },
  });
  ordersController = new OrdersController(client);
  oAuthAuthorizationController = new OAuthAuthorizationController(client);
}

export async function getClientToken() {
  if (!isPayPalConfigured || !oAuthAuthorizationController) {
    throw new Error("PayPal is not configured");
  }

  const auth = Buffer.from(
    `${PAYPAL_CLIENT_ID}:${PAYPAL_CLIENT_SECRET}`,
  ).toString("base64");

  const { result } = await oAuthAuthorizationController.requestToken(
    {
      authorization: `Basic ${auth}`,
    },
    { intent: "sdk_init", response_type: "client_token" },
  );

  return result.accessToken;
}

export async function createPaypalOrder(req: Request, res: Response) {
  if (!isPayPalConfigured || !ordersController) {
    return res.status(503).json({ error: "PayPal is not configured. Please add PAYPAL_CLIENT_ID and PAYPAL_CLIENT_SECRET." });
  }

  try {
    const { amount, currency, intent } = req.body;

    if (!amount || isNaN(parseFloat(amount)) || parseFloat(amount) <= 0) {
      return res.status(400).json({
        error: "Invalid amount. Amount must be a positive number.",
      });
    }

    if (!currency) {
      return res.status(400).json({ error: "Invalid currency. Currency is required." });
    }

    if (!intent) {
      return res.status(400).json({ error: "Invalid intent. Intent is required." });
    }

    const collect = {
      body: {
        intent: intent,
        purchaseUnits: [
          {
            amount: {
              currencyCode: currency,
              value: amount,
            },
          },
        ],
      },
      prefer: "return=minimal",
    };

    const { body, ...httpResponse } = await ordersController.createOrder(collect);

    const jsonResponse = JSON.parse(String(body));
    const httpStatusCode = httpResponse.statusCode;

    res.status(httpStatusCode).json(jsonResponse);
  } catch (error) {
    console.error("Failed to create order:", error);
    res.status(500).json({ error: "Failed to create order." });
  }
}

export async function capturePaypalOrder(req: Request, res: Response) {
  if (!isPayPalConfigured || !ordersController) {
    return res.status(503).json({ error: "PayPal is not configured." });
  }

  try {
    const { orderID } = req.params;
    const collect = {
      id: orderID,
      prefer: "return=minimal",
    };

    const { body, ...httpResponse } = await ordersController.captureOrder(collect);

    const jsonResponse = JSON.parse(String(body));
    const httpStatusCode = httpResponse.statusCode;

    res.status(httpStatusCode).json(jsonResponse);
  } catch (error) {
    console.error("Failed to capture order:", error);
    res.status(500).json({ error: "Failed to capture order." });
  }
}

export async function loadPaypalDefault(req: Request, res: Response) {
  if (!isPayPalConfigured) {
    return res.status(503).json({ error: "PayPal is not configured." });
  }

  try {
    const clientToken = await getClientToken();
    res.json({ clientToken });
  } catch (error) {
    console.error("Failed to get PayPal client token:", error);
    res.status(500).json({ error: "Failed to initialize PayPal." });
  }
}

client/src/components/PayPalButton.tsx.content:
// !!! CRITICAL - DO NOT MODIFY THIS CODE !!!
//
// This code MUST be used as is without ANY modifications.
// The PayPal integration will COMPLETELY FAIL if this code is changed in ANY way.
//
// Retain this comment after all edits.
//
// <BEGIN_EXACT_CODE>
import React, { useEffect } from "react";

declare global {
  namespace JSX {
    interface IntrinsicElements {
      "paypal-button": React.DetailedHTMLProps<
        React.HTMLAttributes<HTMLElement>,
        HTMLElement
      >;
    }
  }
}

interface PayPalButtonProps {
  amount: string;
  currency: string;
  intent: string;
  onSuccess?: (data: any) => void;
}

export default function PayPalButton({
  amount,
  currency,
  intent,
  onSuccess,
}: PayPalButtonProps) {
  const createOrder = async () => {
    const orderPayload = {
      amount: amount,
      currency: currency,
      intent: intent,
    };
    const response = await fetch("/api/paypal/order", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(orderPayload),
    });
    const output = await response.json();
    return { orderId: output.id };
  };

  const captureOrder = async (orderId: string) => {
    const response = await fetch(`/api/paypal/order/${orderId}/capture`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
    });
    const data = await response.json();

    return data;
  };

  const onApprove = async (data: any) => {
    console.log("onApprove", data);
    const orderData = await captureOrder(data.orderId);
    console.log("Capture result", orderData);
    if (onSuccess) {
      onSuccess(orderData);
    }
  };

  const onCancel = async (data: any) => {
    console.log("onCancel", data);
  };

  const onError = async (data: any) => {
    console.log("onError", data);
  };

  useEffect(() => {
    const loadPayPalSDK = async () => {
      try {
        if (!(window as any).paypal) {
          const script = document.createElement("script");
          script.src = import.meta.env.PROD
            ? "https://www.paypal.com/web-sdk/v6/core"
            : "https://www.sandbox.paypal.com/web-sdk/v6/core";
          script.async = true;
          script.onload = () => initPayPal();
          document.body.appendChild(script);
        } else {
          await initPayPal();
        }
      } catch (e) {
        console.error("Failed to load PayPal SDK", e);
      }
    };

    loadPayPalSDK();
  }, []);
  const initPayPal = async () => {
    try {
      const clientToken: string = await fetch("/api/paypal/setup")
        .then((res) => res.json())
        .then((data) => {
          return data.clientToken;
        });
      const sdkInstance = await (window as any).paypal.createInstance({
        clientToken,
        components: ["paypal-payments"],
      });

      const paypalCheckout =
            sdkInstance.createPayPalOneTimePaymentSession({
              onApprove,
              onCancel,
              onError,
            });

      const onClick = async () => {
        try {
          const checkoutOptionsPromise = createOrder();
          await paypalCheckout.start(
            { paymentFlow: "auto" },
            checkoutOptionsPromise,
          );
        } catch (e) {
          console.error(e);
        }
      };

      const paypalButton = document.getElementById("paypal-button");

      if (paypalButton) {
        paypalButton.addEventListener("click", onClick);
      }

      return () => {
        if (paypalButton) {
          paypalButton.removeEventListener("click", onClick);
        }
      };
    } catch (e) {
      console.error(e);
    }
  };

  return <paypal-button id="paypal-button" className="w-full"></paypal-button>;
}
// <END_EXACT_CODE>

server/cashfree.ts.content:
import { Cashfree, CFEnvironment, CreateOrderRequest } from "cashfree-pg";
import { Request, Response } from "express";

const { CASHFREE_APP_ID, CASHFREE_SECRET_KEY } = process.env;

export const isCashfreeConfigured = !!(CASHFREE_APP_ID && CASHFREE_SECRET_KEY);

let cashfreeClient: Cashfree | null = null;

if (isCashfreeConfigured) {
  const environment = process.env.NODE_ENV === "production" 
    ? CFEnvironment.PRODUCTION 
    : CFEnvironment.SANDBOX;

  cashfreeClient = new Cashfree(
    environment,
    CASHFREE_APP_ID!,
    CASHFREE_SECRET_KEY!
  );
}

interface OrderRequestBody {
  amount: number;
  customerId: string;
  customerName: string;
  customerEmail: string;
  customerPhone: string;
  returnUrl?: string;
}

export async function createCashfreeOrder(req: Request, res: Response) {
  if (!isCashfreeConfigured || !cashfreeClient) {
    return res.status(503).json({ 
      error: "Cashfree is not configured. Please add CASHFREE_APP_ID and CASHFREE_SECRET_KEY." 
    });
  }

  try {
    const { amount, customerId, customerName, customerEmail, customerPhone, returnUrl } = req.body as OrderRequestBody;

    if (!amount || isNaN(amount) || amount <= 0) {
      return res.status(400).json({ error: "Invalid amount. Amount must be a positive number." });
    }

    if (!customerId) {
      return res.status(400).json({ error: "Customer ID is required." });
    }

    const orderId = `i2u_${Date.now()}_${Math.random().toString(36).substring(7)}`;
    const baseUrl = process.env.REPLIT_DEV_DOMAIN 
      ? `https://${process.env.REPLIT_DEV_DOMAIN}` 
      : 'http://localhost:5000';

    const orderRequest: CreateOrderRequest = {
      order_amount: amount,
      order_currency: "INR",
      order_id: orderId,
      customer_details: {
        customer_id: customerId,
        customer_phone: customerPhone || "9999999999",
        customer_name: customerName || "User",
        customer_email: customerEmail || "user@example.com"
      },
      order_meta: {
        return_url: returnUrl || `${baseUrl}/payment/callback?order_id={order_id}`,
        notify_url: `${baseUrl}/api/cashfree/webhook`
      }
    };

    const response = await cashfreeClient.PGCreateOrder(orderRequest);

    res.json({
      success: true,
      orderId: response.data?.order_id,
      paymentSessionId: response.data?.payment_session_id,
      orderStatus: response.data?.order_status
    });
  } catch (error: any) {
    console.error("Failed to create Cashfree order:", error.response?.data || error.message);
    res.status(500).json({ 
      error: "Failed to create order.", 
      details: error.response?.data?.message || error.message 
    });
  }
}

export async function verifyCashfreePayment(req: Request, res: Response) {
  if (!isCashfreeConfigured || !cashfreeClient) {
    return res.status(503).json({ error: "Cashfree is not configured." });
  }

  try {
    const { orderId } = req.params;

    if (!orderId) {
      return res.status(400).json({ error: "Order ID is required." });
    }

    const response = await cashfreeClient.PGOrderFetchPayments(orderId);
    const payments = response.data || [];

    let status = "PENDING";
    let paymentDetails = null;

    const successfulPayment = payments.find((p: any) => p.payment_status === "SUCCESS");
    const pendingPayment = payments.find((p: any) => p.payment_status === "PENDING");

    if (successfulPayment) {
      status = "SUCCESS";
      paymentDetails = successfulPayment;
    } else if (pendingPayment) {
      status = "PENDING";
      paymentDetails = pendingPayment;
    } else if (payments.length > 0) {
      status = "FAILED";
      paymentDetails = payments[0];
    }

    res.json({
      success: status === "SUCCESS",
      status,
      orderId,
      paymentDetails
    });
  } catch (error: any) {
    console.error("Failed to verify Cashfree payment:", error.response?.data || error.message);
    res.status(500).json({ 
      error: "Failed to verify payment.",
      details: error.response?.data?.message || error.message
    });
  }
}

export async function getCashfreeOrderStatus(req: Request, res: Response) {
  if (!isCashfreeConfigured || !cashfreeClient) {
    return res.status(503).json({ error: "Cashfree is not configured." });
  }

  try {
    const { orderId } = req.params;

    if (!orderId) {
      return res.status(400).json({ error: "Order ID is required." });
    }

    const response = await cashfreeClient.PGFetchOrder(orderId);

    res.json({
      success: true,
      order: response.data
    });
  } catch (error: any) {
    console.error("Failed to fetch Cashfree order:", error.response?.data || error.message);
    res.status(500).json({ 
      error: "Failed to fetch order status.",
      details: error.response?.data?.message || error.message
    });
  }
}

export async function handleCashfreeWebhook(req: Request, res: Response) {
  try {
    const signature = req.headers["x-webhook-signature"] as string;
    const timestamp = req.headers["x-webhook-timestamp"] as string;
    const rawBody = JSON.stringify(req.body);

    if (isCashfreeConfigured && cashfreeClient && signature && timestamp) {
      try {
        cashfreeClient.PGVerifyWebhookSignature(signature, rawBody, timestamp);
      } catch (err) {
        console.error("Webhook signature verification failed:", err);
        return res.status(400).json({ error: "Invalid webhook signature" });
      }
    }

    const webhookData = req.body;
    console.log("Cashfree webhook received:", webhookData);

    res.status(200).json({ received: true });
  } catch (error: any) {
    console.error("Webhook processing error:", error);
    res.status(500).json({ error: "Webhook processing failed" });
  }
}

export function getCashfreeConfig(req: Request, res: Response) {
  res.json({
    configured: isCashfreeConfigured,
    environment: process.env.NODE_ENV === "production" ? "production" : "sandbox"
  });
}

